class CartFill{constructor(t){this.adder=t,this.addTocart()}addTocart(){$(this.adder).click((function(t){t.preventDefault();console.log(this);let e=$("#item-quantity")[0].value,a=$(this).attr("id").split("-")[1];""!==e?$.ajax({type:"post",url:"/order/add-to-cart",data:{itemId:a,quantity:e},success:function(t){let e=$("#cart-count").text();e=parseInt(e),1==t.data.added&&e++,$("#cart-count").text(e),new Noty({theme:"relax",text:"Item added to cart",type:"success",layout:"topRight",timeout:2e3}).show()},error:function(t){console.log(t)}}):new Noty({theme:"relax",text:"Please add quantity",type:"error",layout:"topRight",timeout:2e3}).show()}))}}